import React, { useRef, useEffect, useState } from 'react';
import socket from "../socketConfig";
import firebase from '../firebase.js';
import { Redirect } from 'react-router-dom';

function Vote(props) {
  const [code, setCode] = useState(props.location.state.code);
  const [name, setName] = useState(props.location.state.name);
  const [pictures, setPictures] = useState([]);
  const [round, setRound] = useState(props.location.state.round);
  const [resultTime, setResultTime] = useState(false);
  const [groupSize, setGroupSize] = useState(props.location.state.groupSize);
  const [vote, setVote] = useState();
  const inputRef = useRef(null);
  const [time, setTime] = useState(20);
  const [word, setWord] = useState();
  const [artist, setArtist] = useState();

  useEffect(() => {
    let numVotes = 0;
    socket.on("vote", () => {
        numVotes ++;
        if (numVotes == groupSize) {
            setResultTime(true);
        }
    });
    const db = firebase.firestore();
    const myAsyncFunc = async() =>{
        await getWord();
        await db.collection("rooms").where("code", "==", code).get().then((querySnapshot) => {
            querySnapshot.docs.forEach((snapshot) => {
                snapshot.ref.collection("rounds").where("round", "==", round).get().then((querySnapshot) => {
                    querySnapshot.docs.forEach((snapshot) => {
                        snapshot.ref.collection("pictures").get().then((querySnapshot) => {
                            querySnapshot.docs.forEach((doc) => {
                                setPictures(pictures => pictures.concat(doc.data().dataURL)).then(() => {
                                    console.log(pictures);
                                })
                            });
                        });
                    });
                });
            });
        });
        const interval = setInterval(() => {
            setTime( time => time - 1);
            if (time == 0) {
                clearInterval(interval);
                setResultTime(true);
            }
        }, 1000);
    }
    myAsyncFunc();
}, []);

  const updateVote = (event) => {
      setVote({vote: event.target.value});
  }

  const submitVote = async() => {
      let votedPicture = pictures[vote.vote];
      console.log(votedPicture);
      const db = firebase.firestore();
      await db.collection("rooms").where("code", "==", code).get().then((querySnapshot) => {
          querySnapshot.docs.forEach((snapshot) => {
              snapshot.ref.collection("rounds", "==", round).get().then((querySnapshot) => {
                  querySnapshot.docs.forEach((snapshot) => {
                      snapshot.ref.collection("pictures").where("dataURL", "==", votedPicture).get().then((querySnapshot) => {
                          querySnapshot.docs.forEach((snapshot) => {
                              snapshot.ref.update({votes : firebase.firestore.FieldValue.increment(1)}).then(() => {
                                  setArtist(snapshot.data().artist);
                                })
                            });
                        });
                    });
                });
            });
        });

        db.collection("rooms").where("code", "==", code).get().then((querySnapshot) => {
            querySnapshot.docs.forEach((snapshot) => {
                snapshot.ref.collection("players").where("name", "==", name).get().then((querySnapshot) => {
                    querySnapshot.docs.forEach((snapshot) => {
                        snapshot.ref.update({
                            votes : firebase.firestore.FieldValue.increment(1)
                        }).then(() => {
                            socket.emit("vote", code);
                        });
                    });
                });
            });
        });
    }

    const getWord = async() => {
        const db = firebase.firestore();
        db.collection("rooms").where("code", "==", code).get().then((querySnapshot) => {
          querySnapshot.docs.forEach((snapshot) => {
            snapshot.ref.collection("rounds").where("round", "==", round).get().then((querySnapshot) => {
              querySnapshot.docs.forEach((snapshot) => {
                  setWord(snapshot.data().word);
              });
          });
      });
    });
    }

  return (
    <div>
        <h1>{time}</h1>
        <h1>{word}</h1>
        {pictures.map((picture, idx) => {
            return (<div>
                        <a href={picture} download>{idx}</a>
                    </div>)
                        })}
        <input type="text" id="vote" onChange={updateVote} ref={inputRef}/>
        <button id="done" onClick={submitVote}>Done1</button>
        {resultTime ? <Redirect to={{pathname: '/result', state: {code: code, name: name, groupSize: groupSize, round: round}}}/> : null }
    </div>
    );
}

export default Vote;
