import React, { useRef, useEffect, useState } from 'react';
import { Redirect } from 'react-router-dom';
import socket from "../socketConfig";
import firebase from '../firebase.js';

function Game(props) {

  const canvasRef = useRef(null)
  const contextRef = useRef(null)
  const [isDrawing, setIsDrawing] = useState(false)
  const [time, setTime] = useState(20);
  const [code, setCode] = useState(props.location.state.code);
  const [name, setName] = useState(props.location.state.name);
  const [round, setRound] = useState(props.location.state.round);
  const [voteTime, setVoteTime] = useState(false);
  const [voteTime2, setVoteTime2] = useState(false);
  const [groupSize, setGroupSize] = useState(props.location.state.groupSize);
  const [word, setWord] = useState();

  useEffect(async() => {
    let pictures = 0;
    socket.on('picture', () => {
      pictures ++;
      if (pictures == groupSize) {
        setVoteTime(true);
      }
    });
    const canvas = canvasRef.current;
    canvas.width = window.innerWidth * 2;
    canvas.height = window.innerHeight * 2;
    canvas.style.width = `${window.innerWidth}px`;
    canvas.style.height = `${window.innerHeight}px`;
    const context = canvas.getContext("2d")
    context.scale(2,2)
    context.lineCap = "round"
    context.strokeStyle = "black"
    context.lineWidth = 5
    contextRef.current = context;
    let time2 = 20;
    const interval = setInterval(() => {
      time2 = time2 - 1;
      setTime(time => time - 1);
      console.log(time);
      if (time2 == 19) {
        getWord();
      }
      if (time2 == 0) {
        addPicture();
        clearInterval(interval);
      }
    }, 1000);
  }, []);

const addPicture = async() => {
  console.log("time has run out");
  let dataURL = canvasRef.current.toDataURL();
  const db = firebase.firestore();
  db.collection("rooms").where("code", "==", code).get().then((querySnapshot) => {
    querySnapshot.docs.forEach((snapshot) => {
      snapshot.ref.collection("rounds").where("round", "==", round).get().then((querySnapshot) => {
        querySnapshot.docs.forEach((snapshot) => {
          snapshot.ref.collection("pictures").add({artist: name, dataURL: dataURL, votes: 0}).then(() => {
            //socket.emit("picture", code);
            setVoteTime2(true);
          });
        });
      });
    });
  });
}

const getWord = async() => {
  const db = firebase.firestore();
    db.collection("rooms").where("code", "==", code).get().then((querySnapshot) => {
      querySnapshot.docs.forEach((snapshot) => {
        snapshot.ref.collection("rounds").where("round", "==", round).get().then((querySnapshot) => {
          querySnapshot.docs.forEach((snapshot) => {
            setWord(snapshot.data().word);
          });
        });
      });
    });
  }
  const startDrawing = ({nativeEvent}) => {
    const {offsetX, offsetY} = nativeEvent;
    contextRef.current.beginPath()
    contextRef.current.moveTo(offsetX, offsetY)
    setIsDrawing(true)
  }

  const finishDrawing = () => {
   contextRef.current.closePath()
   setIsDrawing(false)
  }

  const draw = ({nativeEvent}) => {
    if(!isDrawing){
      return
    }
    const {offsetX, offsetY} = nativeEvent;
    contextRef.current.lineTo(offsetX, offsetY)
    contextRef.current.stroke()
  }

  return (
    <div>
        <h1>{time}</h1>
        <h2>{word}</h2>
        <canvas
        onMouseDown={startDrawing}
        onMouseUp={finishDrawing}
        onMouseMove={draw}
        ref={canvasRef}
        />
        {voteTime2 ? <Redirect to={{pathname: '/vote', state: {code: code, name: name, round: round, groupSize: groupSize}}}/> : null }
    </div>
    );
}

export default Game;
