import React from 'react'
import { Redirect } from 'react-router-dom'
import socket from "../socketConfig";
import firebase from '../firebase.js'

class Create extends React.Component {

    constructor() {
      super();
      this.state = {code: '', name: '', hasCreated: false};
    }

    componentDidMount() {
      socket.on('groupCompleted', () => {
        this.setState({hasCreated: true});
      })
    }

    makeId = () => {
      var result           = '';
      var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
      var charactersLength = characters.length;
      for ( var i = 0; i < 4; i++ ) {
         result += characters.charAt(Math.floor(Math.random() * charactersLength));
      }
      this.setState({code: result});
      console.log(result);
      return result;
   }

    createGroup = () => {
      const db = firebase.firestore();
      db.collection("rooms").add({
        code: this.state.code,
        words: []
      }).then(() => {
        let docRef = db.collection("rooms").where("code", "==", this.state.code);
        docRef.collection("players").add({name: this.state.name, votes: 0}).then(() => {
          socket.emit("createGroup", this.state.code);
        })
    });
  }

    updateName = (event) => {
      this.setState({name: event.target.value});
    }

    render() {
      return (
        <div>
          <label for="code" id="l1">Room Code:</label>
          <input type="text" id="code" value={this.makeId} readonly/>
          <label for="name" id="l2">Name</label>
          <input type="text" id="name" onChange={this.updateName}/>
          <button id="Done1" onClick={this.createGroup}>Done1</button>
          { this.state.hasCreated ? <Redirect to={{pathname: '/room', state: {code: this.state.code, name: this.state.name, hasCreated: this.state.hasCreated}}}/> : null }
        </div>
      )
    }
  }

  export default Create;
