import React, { useRef, useEffect, useState } from 'react';
import { Redirect } from 'react-router-dom';
import socket from "../socketConfig";
import firebase from '../firebase.js';

function Game(props) {

  const canvasRef = useRef(null)
  const contextRef = useRef(null)
  const [isDrawing, setIsDrawing] = useState(false)
  const [time, setTime] = useState(20);
  const code = useState(props.location.state.code);
  const name = useState(props.location.state.name);
  const round = useState(props.location.state.round);
  const [voteTime, setVoteTime] = useState(false);
  const groupSize = useState(props.location.state.groupSize);
  const [word, setWord] = useState();

  useEffect(() => {
    let pictures = 0;
    socket.on('picture', () => {
      pictures ++;
      if (pictures == groupSize) {
        setVoteTime(true);
      }
    });
    const canvas = canvasRef.current;
    canvas.width = window.innerWidth * 2;
    canvas.height = window.innerHeight * 2;
    canvas.style.width = `${window.innerWidth}px`;
    canvas.style.height = `${window.innerHeight}px`;

    const context = canvas.getContext("2d")
    context.scale(2,2)
    context.lineCap = "round"
    context.strokeStyle = "black"
    context.lineWidth = 5
    contextRef.current = context;
    getWord().then((word) => {
      setWord(word);
    });
    const interval = setInterval(() => {
        setTime( time => time - 1);
        if (time == 0) {
            clearInterval(interval);
            let dataURL = canvasRef.current.toDataURL();
            const db = firebase.firestore();
            db.collection("rooms").where("code", "==", code).collection("rounds").where("round", "==", round).collection("pictures").add({artist: name, dataURL: dataURL, votes: 0}).then(() => {
              socket.emit("picture", code);
            });
        }
    }, 1000);
  }, []);

  const getWord = async() => {
    const db = firebase.firestore();
    db.collection("rooms").where("code", "==", code).collection("rounds").where("round", "==", round).get().then((doc) => {
      return doc.data.word;
    });
  }

  const startDrawing = ({nativeEvent}) => {
    const {offsetX, offsetY} = nativeEvent;
    contextRef.current.beginPath()
    contextRef.current.moveTo(offsetX, offsetY)
    setIsDrawing(true)
  }

  const finishDrawing = () => {
   contextRef.current.closePath()
   setIsDrawing(false)
  }

  const draw = ({nativeEvent}) => {
    if(!isDrawing){
      return
    }
    const {offsetX, offsetY} = nativeEvent;
    contextRef.current.lineTo(offsetX, offsetY)
    contextRef.current.stroke()
  }

  return (
    <div>
        <h1>{time}</h1>
        <h2>{word}</h2>
        <canvas
        onMouseDown={startDrawing}
        onMouseUp={finishDrawing}
        onMouseMove={draw}
        ref={canvasRef}
        />
        {voteTime ? <Redirect to={{pathname: '/vote', state: {code: code, name: name, round: round, groupSize: groupSize}}}/> : null }}
    </div>
    );
}

export default Game;
