import React, { useRef, useEffect, useState } from 'react';
import socket from "../socketConfig";
import firebase from '../firebase.js';
import { Redirect } from 'react-router-dom';

function Result(props) {
  const code = useState(props.location.state.code);
  const name = useState(props.location.state.name);
  const [winningPic, setWinningPic] = useState();
  const [winningArtist, setWinningArtist] = useState();
  const round = useState(props.location.state.round);
  const [nextRoundTime, setNextRoundTime] = useState(false);
  const [finalScoreTime, setFinalScoreTime] = useState(false);
  const groupSize = useState(props.location.state.groupSize);
  const [word, setWord] = useState();

  useEffect(() => {
    const db = firebase.firestore();
    db.collection("rooms").where("code", "==", code).collection("rounds").where("round", "==", round).collection("pictures").orderBy("votes", com.google.firebase.firestore.Query.Direction.DESCENDING).limit(1).get().then((doc) => {
        setWinningPic(doc.data().dataURL);
        setWinningArtist(doc.data().artist);
        db.collection("rooms").where("code", "==", code).collection("rounds").where("round", "==", round).get().then((doc) => {
            setWord(doc.data().word);
        });
    });
    socket.on("nextRound", () => {
        setNextRoundTime(true);
    });
    socket.on("finalScore", () => {
        setFinalScoreTime(true);
    });

}, []);

const nextRound = () => {
    if (round + 1 <= 5) {
        socket.emit("nextRound", code);
    }
    else {
        socket.emit("finalScore", code);
    }

}



  return (
    <div>
        <h1>{word}</h1>
        <img src={winningPic} id={winningPic}/>
        <label htmlFor={winningPic}>{winningArtist}</label>
        <button id="done" onClick={nextRound}>Next Round</button>
        {nextRoundTime ? <Redirect to={{pathname: '/game', state: {code: code, name: name, groupSize: groupSize, round: round + 1}}}/> : null }}
        {finalScoreTime ? <Redirect to={{pathname: '/final', state: {code: code, name: name, groupSize: groupSize, round: round + 1}}}/> : null }}
    </div>
    );
}

export default Result;
