import React, { useRef, useEffect, useState } from 'react';
import socket from "../socketConfig";
import firebase from '../firebase.js';
import { Redirect } from 'react-router-dom';

function Vote(props) {
  const code = useState(props.location.state.code);
  const name = useState(props.location.state.name);
  const [pictures, setPictures] = useState([]);
  const round = useState(props.location.state.round);
  const [resultTime, setResultTime] = useState(false);
  const groupSize = useState(props.location.state.groupSize);
  const [vote, setVote] = useState();
  const inputRef = useRef(null);
  const [time, setTime] = useState(20);

  useEffect(() => {
    const db = firebase.firestore();
    db.collection("rooms").where("code", "==", code).get().then((querySnapshot) => {
        querySnapshot.docs.forEach((snapshot) => {
            snapshot.ref.collection("rounds").where("round", "==", round).get().then((querySnapshot) => {
                querySnapshot.docs.forEach((snapshot) => {
                    snapshot.ref.collection("pictures").get().then((querySnapshot) => {
                        querySnapshot.docs.forEach((doc) => {
                            setPictures(pictures => pictures.concat(doc.data().dataURL));
                        });
                    });
                });
            });
        });
    });
    let numVotes = 0;
    socket.on("vote", () => {
        numVotes ++;
        if (numVotes == groupSize) {
            setResultTime(true);
        }
    });
    const interval = setInterval(() => {
        setTime( time => time - 1);
        if (time == 0) {
            clearInterval(interval);
            setResultTime(true);
        }
    }, 1000);
}, []);

  const updateVote = (event) => {
      setVote({vote: event.target.value});
  }

  const submitVote = () => {
      let votedPicture = pictures[vote];
      const db = firebase.firestore();
      db.collection("rooms").where("code", "==", code).collection("rounds").where("round", "==", round).collection("pictures").where("dataURL", "==", votedPicture).update({
          votes : firebase.firestore.FieldValue.increment(1)
      }).then(() => {
        db.collection("rooms").where("code", "==", code).collection("rounds").where("round", "==", round).collection("pictures").where("dataURL", "==", votedPicture).get().then((doc) => {
            db.collection("rooms").where("code", "==", code).collection("players").where("name", "==", doc.data().artist).update({
                votes : firebase.firestore.FieldValue.increment(1)
            }).then(() => {
                socket.emit("vote", code);
            });
        });
    });
}

  const getWord = () => {
      const db = firebase.firestore();
      db.collection("rooms").where("code", "==", code).collection("rounds").where("round", "==", round).get().then((doc) => {
          return doc.data().word;
      });
  }

  return (
    <div>
        <h1>{time}</h1>
        <h1>{getWord}</h1>
        {pictures.map((idx, picture) => {
            return (<div>
                        <img src={picture} id={picture}/>
                        <label htmlFor={picture}>{idx}</label>
                    </div>)
                        })}
        <input type="text" id="vote" onChange={updateVote} ref={inputRef}/>
        <button id="done" onClick={submitVote}>Done1</button>
        {resultTime ? <Redirect to={{pathname: '/result', state: {code: code, name: name, groupSize: groupSize, round: round}}}/> : null }}
    </div>
    );
}

export default Vote;
