import React from 'react';
import { Redirect } from 'react-router-dom';

class Home extends React.Component{
    constructor() {
        super();
        this.state = {create: false, join: false};
    }
    renderCreate() {
        this.setState({create: true});
    }
    renderJoin() {
        this.setState({join: true});
    }
    render () {
        return (
            <div>
                <h1>Quipdraw (Jackbox Games please buy us)</h1>
                <button id="create" onClick={this.renderCreate}>Create Room</button>
                { this.state.create ? <Redirect to="/create"/> : null }
                <button id="join" onClick={this.renderJoin}>Join Room</button>
                { this.state.join ? <Redirect to="/join"/> : null }
            </div>
        )
    }
}

export default Home;
